mapper.insert:
INSERT INTO tbl_member (

)VALUES(
		  #{email}
		, #{nickname}
		, #{password}
		, NOW()
		, NOW()
		, NOW()
		, 0
		, 'Y'
		, 'ROLE_USER'
)

mapper.update:
UPDATE tbl_member
SET
		  update_date = NOW()
		, nickname = #{nickname}
		, password = #{password}
		, enabled = #{enabled}
		, levels = #{levels}
WHERE
		email = #{email}

mapper.select:
SELECT count(*)
FROM tbl_member
WHERE email=#{email} AND password =#{password}
